import "./Expense.css";
import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItems";
import ExpenseFilter from "./ExpenseFilter";
import { useState } from "react";

function Expense(props) {
  const[originalYear,setFilteredYear] = useState('2020');
  
  const newYearHandler = (year) =>{
  setFilteredYear(year);
  }
  return (
    <div>
      <Card className="expenses">
        <ExpenseFilter selected={originalYear} onChangeFilter = {newYearHandler}/>
        <ExpenseItem
          title={props.eArray[0].title}
          date={props.eArray[0].date}
          amount={props.eArray[0].amount}
        ></ExpenseItem>
        <ExpenseItem
          title={props.eArray[1].title}
          date={props.eArray[1].date}
          amount={props.eArray[1].amount}
        ></ExpenseItem>
        <ExpenseItem
          title={props.eArray[2].title}
          date={props.eArray[2].date}
          amount={props.eArray[2].amount}
        ></ExpenseItem>
        <ExpenseItem
          title={props.eArray[3].title}
          date={props.eArray[3].date}
          amount={props.eArray[3].amount}
        ></ExpenseItem>
      </Card>
    </div>
  );
}

export default Expense;
